import React from 'react';
import Image from 'next/image';
import { Sport } from '@/lib/types';
import ScheduleTable from './ScheduleTable';
import PdfSelector from './PdfSelector';

interface SportPageProps {
  sport: Sport;
}

export default function SportPage({ sport }: SportPageProps) {
  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative min-h-[40vh] max-h-[60vh] flex items-center justify-center max-w-7xl mx-auto overflow-hidden rounded-lg mt-8">
        <div className="absolute inset-0">
          <Image
            src={sport.heroImage}
            alt={`Image hero de ${sport.name}`}
            fill
            className={`object-cover ${sport.slug === 'gym' ? 'object-[center_25%]' : 'object-center'}`}
            priority
            sizes="100vw"
          />
          <div className="absolute inset-0 bg-black bg-opacity-40"></div>
        </div>
        <div className="relative z-10 text-center text-white px-4">
          {sport.logo && (
            <div className="mb-4 flex justify-center">
              <div className="w-12 h-12 sm:w-16 sm:h-16 md:w-20 md:h-20 flex items-center justify-center hover:scale-105 transition-transform duration-300">
                <Image
                  src={sport.logo}
                  alt={`Logo ${sport.name}`}
                  width={80}
                  height={80}
                  className="object-contain"
                />
              </div>
            </div>
          )}
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-bold mb-4">{sport.name}</h1>
        </div>
      </section>

      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Introduction */}
